#!/bin/bash

set -e

ROOTFS_DIR="$1"
ASSETS_DIR="$(dirname "$0")/../assets"

if [[ -z "$ROOTFS_DIR" ]]; then
    echo "Usage: $0 <rootfs_directory>"
    exit 1
fi

log() {
    echo "[BRANDING] $1"
}

log "Applying Kali branding to $ROOTFS_DIR"

# Update OS release information
cat > "$ROOTFS_DIR/etc/os-release" << 'EOF'
PRETTY_NAME="Kali Linux Mobile"
NAME="Kali Linux"
VERSION_ID="2024.1"
VERSION="2024.1"
VERSION_CODENAME=kali-rolling
ID=kali
ID_LIKE=debian
HOME_URL="https://www.kali.org/"
SUPPORT_URL="https://forums.kali.org/"
BUG_REPORT_URL="https://bugs.kali.org/"
PRIVACY_POLICY_URL="https://www.kali.org/privacy/"
LOGO=kali-logo
EOF

# Update LSB release
cat > "$ROOTFS_DIR/etc/lsb-release" << 'EOF'
DISTRIB_ID=Kali
DISTRIB_RELEASE=2024.1
DISTRIB_CODENAME=kali-rolling
DISTRIB_DESCRIPTION="Kali GNU/Linux Rolling"
EOF

# Update hostname
echo "kali-mobile" > "$ROOTFS_DIR/etc/hostname"

# Update hosts file
cat > "$ROOTFS_DIR/etc/hosts" << 'EOF'
127.0.0.1	localhost
127.0.1.1	kali-mobile

# The following lines are desirable for IPv6 capable hosts
::1     localhost ip6-localhost ip6-loopback
ff02::1 ip6-allnodes
ff02::2 ip6-allrouters
EOF

# Create Kali user instead of droidian
mkdir -p "$ROOTFS_DIR/etc/skel"
mkdir -p "$ROOTFS_DIR/home/kali"

# Copy branding assets
if [[ -f "$ASSETS_DIR/kali-logo.png" ]]; then
    cp "$ASSETS_DIR/kali-logo.png" "$ROOTFS_DIR/usr/share/pixmaps/"
fi

if [[ -f "$ASSETS_DIR/kali-wallpaper.jpg" ]]; then
    cp "$ASSETS_DIR/kali-wallpaper.jpg" "$ROOTFS_DIR/usr/share/backgrounds/"
fi

# Update desktop environment branding
mkdir -p "$ROOTFS_DIR/etc/dconf/db/local.d"
cat > "$ROOTFS_DIR/etc/dconf/db/local.d/01-kali-branding" << 'EOF'
[org/gnome/desktop/background]
picture-uri='file:///usr/share/backgrounds/kali-wallpaper.jpg'
picture-uri-dark='file:///usr/share/backgrounds/kali-wallpaper.jpg'

[org/gnome/desktop/interface]
gtk-theme='Kali-Dark'
icon-theme='Flat-Remix-Blue-Dark'

[org/gnome/shell]
favorite-apps=['org.gnome.Nautilus.desktop', 'firefox-esr.desktop', 'org.gnome.Terminal.desktop']
EOF

# Update GDM branding
mkdir -p "$ROOTFS_DIR/etc/gdm3"
cat > "$ROOTFS_DIR/etc/gdm3/custom.conf" << 'EOF'
[daemon]
WaylandEnable=false

[security]

[xdmcp]

[chooser]

[debug]
EOF

# Create welcome message
cat > "$ROOTFS_DIR/etc/motd" << 'EOF'
┏━(Message Of The Day)
┗━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━┓
